mapper.select:
SELECT * FROM video_info WHERE deleted = 0 AND status = 1

    AND language = #{language}


    AND level = #{level}


    AND category_id = #{categoryId}

ORDER BY id DESC

SELECT * FROM video_info WHERE deleted = 0 AND status = 1

    AND (title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))


    AND language = #{language}


    AND level = #{level}

ORDER BY id DESC
